<template>
  <div
    class="window-app"
    v-on:click="clickWindow"
    draggable="true"
    v-on:dragend="dragend"
  >
    <div class="window-app-btns">
      <div v-for="(element, index) in getElementKeys" v-bind:key="index">
        <div class="div-app" v-if="getElement[element].on" v-on:click="(event) => windowEvent(event, element, getElement[element])">
          <img class="img" v-bind:src="getElement[element].src" v-bind:alt="getElement[element].alt">
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: '',
  props: {
    variables: {
      type: Object,
      required: true
    }
  },
  mounted() {
    global.listener.on('get-window-division', (data, callback) => {
      if (data.id == this.variables.component) {
        callback({
          func: this.windowEvent,
          division: this.component.division
        });
      }
    });
    this.render();
    global.listener.push(this.resize);
  },
  data() {
    return {
      component: {
        division: null
      },
      apps: [],
      elements: {
        minimize: {
          on: true,
          alt: "minimize image",
          src: "data:image/png;base64,"
        },
        maximize: {
          on: true,
          alt: "",
          src: "data:image/png;base64,"
        },
        center: {
          on: false,
          alt: "",
          src: "data:image/png;base64,"
        },
        close: {
          on: true,
          alt: "",
          src: "data:image/png;base64,"
        },
      }
    }
  },
  methods: {
    async render() {
      if (!this.component.division)
        this.component.division = await global.listener.getDiv(this.variables.component);
      global.listener.pushWindow(this.component.division, Object.assign({}, this.variables.app, { id: this.variables.component }));
    },
    resize(event, data = { innerWidth: 0, innerHeight: 0 }) {
      if (this.elements.center.on) {
        global.listener.maximize(this.component.division, this.variables.style, true);
      } else if (this.elements.maximize.on) {
        global.listener.center(this.component.division, this.variables.style, true);
      }
    },
    clickWindow(event) {
      global.listener.maxPositionZ(this.component.division);
    },
    maximize() {
      this.elements.center.on = true;
      this.elements.maximize.on = false;
    },
    center() {
      this.elements.maximize.on = true;
      this.elements.center.on = false;
    },
    async windowEvent(event, trigger) {
      switch (trigger) {
        case 'minimize':
          this.$emit('listiner', { trigger: trigger, show: false, id: this.variables.component });
          break;
        case 'maximize':
          this.maximize();
          global.listener.maximize(this.component.division, this.variables.style);
          this.$emit('listiner', { trigger: trigger, id: this.variables.component });
          break;
        case 'center':
          this.center();
          global.listener.center(this.component.division, this.variables.style);
          break;
        case 'close':
          global.listener.remove(this.resize);
          global.listener.removeWindow(this.component.division, this.variables.app);
          global.listener.emit('close-window', { bool: false, name: this.variables.name });
          break;
      }
    },
    async dragend(event) {
      if (!this.component.division)
        this.component.division = await global.listener.getDiv(this.variables.component);

      this.center();
      global.listener.dragend({
        x: event.x,
        y: event.y,
        srcElement: this.component.division
      }, this.variables.style);
    }
  },
  computed: {
    getElement() {
      return this.elements;
    },
    getElementKeys() {
      return Object.keys(this.getElement);
    }
  }
}
</script>

<style lang="scss">
.window-app {
  width: 100%;
  height: 20px;
  background-color: #2c3e50;
  text-align: right;

  .window-app-btns {
    display: flex;
    flex-direction: row;
    align-items: flex-end;
    justify-content: flex-end;

    .div-app {
      width: 16px;
      height: 16px;
      padding: 2px;
      -webkit-border-radius: 20px;
      
      .img {
        width: 16px;
        height: 16px;
        cursor: pointer;
      }
      .img:hover {
        opacity: 0.6;
      }
    }

    .div-app:hover {
      background-color: #34495e;
    }
  }
}
</style>